/*Table structure for table `author` */

DROP TABLE IF EXISTS `author`;

CREATE TABLE `author` (
  `authorid` int(11) NOT NULL auto_increment COMMENT '编号',
  `authorname` varchar(50) NOT NULL COMMENT '名称',
  `authorprice` double default NULL COMMENT '金额',
  `collectionbook` int(11) default NULL COMMENT '收藏的小说id',
  `releasebook` int(11) default NULL COMMENT '发布的小说id',
  `authorpwd` varchar(255) default NULL COMMENT '用户密码',
  `email` varchar(20) default NULL COMMENT '邮箱',
  `phone` varchar(20) default NULL COMMENT '电话',
  PRIMARY KEY  (`authorid`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

