/*Table structure for table `collection` */

DROP TABLE IF EXISTS `collection`;

CREATE TABLE `collection` (
  `bookid` int(11) default NULL COMMENT '小说id',
  `authorid` int(11) default NULL COMMENT '收藏人id'
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

/*Data for the table `collection` */

insert  into `collection`(`bookid`,`authorid`) values (175,6),(185,6),(187,6);

/*Table structure for table `comment` */

DROP TABLE IF EXISTS `comment`;

CREATE TABLE `comment` (
  `commentid` int(11) NOT NULL auto_increment COMMENT '评论id',
  `authorid` int(11) default NULL COMMENT '评论人id',
  `commenttime` datetime default NULL COMMENT '评论时间',
  `content` varchar(255) default NULL COMMENT '评论内容',
  `bookid` int(11) default NULL COMMENT '评论小说id',
  PRIMARY KEY  (`commentid`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

/*Data for the table `comment` */

insert  into `comment`(`commentid`,`authorid`,`commenttime`,`content`,`bookid`) values (25,4,'2019-09-14 22:09:56','还可以吧',157),(26,4,'2019-09-14 22:10:51','还行',157),(27,4,'2019-09-14 22:11:25','不喜欢',1),(28,6,'2019-09-15 09:35:23','真难看',176),(29,6,'2019-09-15 10:43:58','难看',1),(31,6,'2019-09-16 09:46:15','很好看',183),(32,6,'2019-09-16 09:46:22','一般',183),(45,6,'2019-09-17 16:20:39','挺好看',1),(46,6,'2019-09-17 17:03:18','真难看',185),(47,6,'2019-09-18 08:25:32','真难看。。。',157),(48,10,'2019-09-18 09:27:23','不咋样呀\n',150),(49,10,'2019-09-18 09:28:39','23333',150),(50,10,'2019-09-18 09:30:09','OVO',150),(51,10,'2019-09-18 09:32:56','888',150),(52,6,'2019-09-18 10:27:22','没钱看',187),(53,6,'2019-09-18 10:27:40','有钱了',187);

/*Table structure for table `consumptionrecord` */

DROP TABLE IF EXISTS `consumptionrecord`;

CREATE TABLE `consumptionrecord` (
  `id` int(11) NOT NULL auto_increment,
  `chapterid` int(11) NOT NULL COMMENT '消费章节',
  `authorid` int(11) NOT NULL COMMENT '消费用户',
  `consumptiontime` datetime NOT NULL COMMENT '消费时间',
  `consumptionamount` int(11) NOT NULL COMMENT '消费金额',
  PRIMARY KEY  (`id`)
