/*Table structure for table `novels` */

DROP TABLE IF EXISTS `novels`;

CREATE TABLE `novels` (
  `bookid` int(100) NOT NULL auto_increment COMMENT '小说id',
  `bookname` varchar(50) default NULL COMMENT '小说名称',
  `booktype` int(100) default NULL COMMENT '小说类型',
  `booktitle` varchar(50) default NULL COMMENT '小说标题',
  `bookoutline` varchar(150) default NULL COMMENT '小说概要',
  `authorid` int(100) default NULL COMMENT '小说作者',
  `bookstate` varchar(50) default NULL COMMENT '小说状态  连载，已完成',
  `ischarge` varchar(50) default NULL COMMENT '是否收费',
  `platform` varchar(50) default NULL COMMENT '发布平台',
  `booknumber` int(255) default NULL COMMENT '小说字数',
  `clicks` int(255) default NULL COMMENT '小说点击率',
  `image` varchar(255) default NULL COMMENT '小说图片',
  `sendword` varchar(50) default NULL COMMENT '作者寄语',
  `bookshouzimu` varchar(10) default NULL COMMENT '首字母',
  PRIMARY KEY  (`bookid`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8;

